import React, { useState, useEffect } from 'react'
import { Button } from '../ButtonElement';
import {
    MenuSectionContainer,
    MenuSectionWrapper,
    MenuSectionRow,
    Column1,
    Column2,
    TextWrapper,
    TopLine,
    Heading,
    Subtitle,
    BtnWrap,
    ImgWrap,
    Img,
    CardContainer,
    StyledCardMedia,
    StyledCard,
    StyledTypography

} from './MenuSectionElements';
import { Grid } from '@material-ui/core';
import MenuCard from './MenuCard';
import axios from 'axios';

const MenuSection = ({lightBg, id, imgStart, topLine, lightText, headline, darkText, description, buttonLabel, img, alt, primary, dark, dark2}) => {


    const [products, setProducts] = useState([]);

    const getProducts = async () => {
        const response = await axios.get('http://127.0.0.1:8000/api/product/')
        setProducts(response.data)
    }

    useEffect(() =>{
        getProducts();
    }, [])

    return (
        <>
        <MenuSectionContainer lightBg={lightBg} id={id}>
            <MenuSectionWrapper>
                <MenuSectionRow imgStart={imgStart}>
                    <Column1>
                        {/* <CardContainer>
                            <StyledCardMedia>
                                <StyledCard>
                                    <StyledTypography>
                                        Hello
                                    </StyledTypography>
                                </StyledCard>
                            </StyledCardMedia>
                        </CardContainer> */}
                        <main className="">
                            <div className=""/>
                            <Grid container justify="center" spacing={4}>
                                {products.map((product) => (
                                    <Grid item key={product.id} xs={12} sm={6} md={4} lg={3}>
                                        <MenuCard product={product} />
                                    </Grid>
                                ))}
                            </Grid>
                        </main>
                    </Column1>
                    <Column2>
                        <ImgWrap>
                            <Img src={img} alt={alt}/>
                        </ImgWrap>
                    </Column2>
                </MenuSectionRow>
            </MenuSectionWrapper>
        </MenuSectionContainer>
        </>
    )
}

export default MenuSection;
