/* eslint-disable no-undef */
import axios from 'axios';
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { StyledContactFormContainer, StyledContactForm, StyledInput, StyledFieldSet, StyledTextArea, StyledError, StyledButton, StyledH2 , StyledLabel } from './ContactFormElements';
import validate from './validateInfo';
import useForm from './useForm';


const Form = () => {

    const { handleChange, handleSubmit, addContactForm, values, errors } = useForm(
        submitForm,
        validate
    );

    return (
        <>
            <StyledContactFormContainer>
            <StyledH2>Message Us!</StyledH2>
                <StyledContactForm onSubmit={handleSubmit}>
                    <label htmlFor="firstName"></label>
                    <StyledInput 
                        type="text" 
                        name="firstName" 
                        placeholder="First Name" 
                        value={values.firstName}
                        onChange={handleChange} 
                        
                        />
                    <label htmlFor="lastName"></label>
                    <StyledInput 
                        type="text" 
                        name="lastName" 
                        placeholder="Last Name" 
                        value={values.lastName} 
                        onChange={handleChange} 

                        />
                    <br />
                    <label htmlFor="phoneNumber"></label>
                    <StyledInput 
                        type="text" 
                        name="phoneNumber" 
                        placeholder="Phone Number" 
                        value={values.phoneNumber} 
                        onChange={handleChange}

                        />
                    <label htmlFor="email"></label>
                    <StyledInput 
                        type="email" 
                        name="email" 
                        placeholder="Email" 
                        defaultValue={email} 
                        onChange={handleChange}
                        
                        />
                    <StyledFieldSet>
                        <legend>Contact Type</legend>
                        <label>
                            <input type="radio" value="Question" name="contactType" onChange={handleChange} />
                            Question
                        </label>
                        <label>
                            <input type="radio" value="Concern" name="contactType" onChange={handleChange} />
                            Concern
                        </label>
                        <label>
                            <input type="radio" value="Feedback" name="contactType" onChange={handleChange} />
                            Feedback
                        </label>
                    </StyledFieldSet>
                    <StyledLabel htmlFor="message">Message:</StyledLabel>
                    <br />
                    <StyledTextArea name="message" placeholder="Give us a little more info about your contact in order to better assist you!" defaultValue={message} onChange={handleChange} />
                    <br />
                    <StyledError>
                        <p>Error Message</p>
                    </StyledError>
                    <StyledButton onClick={addContactForm}>Submit Contact</StyledButton>
                </StyledContactForm>
            </StyledContactFormContainer>
        </>
    );      
};

export default Form;
