import React, { useState, useEffect, Component } from 'react';
import axios from 'axios';
import { StyledCarouselComponent, StyledCarouselItem } from './CarouselElements';

const CarouselComponent = () => {

    const [reviews, setReviews] = useState([])

    const getReviews = async () => {
        const response = await axios.get('http://127.0.0.1:8000/api/reviewform/')
        setReviews(response.data)
    }

    useEffect(() =>{
        getReviews();
    }, [])

    return (
        <>
            <StyledCarouselComponent autoplay={ true } autoplay_speed={ 5000 }>
                <StyledCarouselItem>
                    <p>1</p>
                </StyledCarouselItem>
                <StyledCarouselItem>
                    <p>2</p>
                </StyledCarouselItem>
                <StyledCarouselItem>
                    <p>3</p>
                </StyledCarouselItem>
                <StyledCarouselItem>
                    <p>4</p>
                </StyledCarouselItem>
                <StyledCarouselItem>
                    <p>5</p>
                </StyledCarouselItem>
            </StyledCarouselComponent>
        </>
    )
}

export default CarouselComponent



