import React, {Component} from 'react';
import ReactCardFlip from 'react-card-flip';
import Form from './Form';
import background from '../../images/onlinecatering.png';

class MenuCard extends Component<Form, Front> {
    constructor(props) {
      super(props);
      this.state = {
        isFlipped: false
      };
      this.handleClick = this.handleClick.bind(this);
    }

    handleClick(event) {
      event.preventDefault();
      this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
    }

    render() {
      return (
        <ReactCardFlip isFlipped={this.state.isFlipped}>
          <div style={this.props.styles.card}>
            <img
              style={this.props.styles.image}
              src=""
              alt="Broken Link"
            />

            <button onClick={this.handleClick}>Submit A Request</button>
          </div>

          <div style={this.props.styles.card}>
            <img
              style={this.props.styles.image}
              src=""
              alt="Broken Link"
            />

            <button onClick={this.handleClick}>Flip Card</button>
          </div>
        </ReactCardFlip>
      );
    }
  }

  export default MenuCard;
