import React, { useState } from 'react';
import { ReviewFormContainer, ReviewFormWrapper, ReviewFormContent, ReviewFormFormField } from './ReviewFormElements';

const ReviewForm = () => {
    const [formData, setFormData] = useState({})

    const updateInput = e => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        })
    }

    return (
        <>
            <ReviewFormContainer>
                <ReviewFormWrapper>
                    <ReviewFormContent>

                    </ReviewFormContent>
                    <ReviewFormFormField>
                        {/*
                        First Name
                        Last Initial
                        Date Visited 
                        Food Rating
                        Service Rating
                        Review Message
                        */}

                        <form>
                            <div>
                                <label>
                                First Name:
                                <input
                                    type="text"
                                    name="firstName"
                                    placeholder="First Name"
                                    onChange={updateInput}
                                    value={formData.firstName || ''}
                                />
                                </label>
                            </div>
                        </form>
                    </ReviewFormFormField>
                </ReviewFormWrapper>
            </ReviewFormContainer>
        </>
    )
}

export default ReviewForm
