import React, { useState } from 'react';
import { CateringFormContainer, CateringFormImageContainer, CateringFormWrapper, H1, CateringSectionBg, ImageBg, StyledFrontCardContainer, StyledImage, StyledBackCardButton, StyledFrontCardButton, StyledBackCardContainer, StyledModal } from './CateringFormElements';
import background from '../../images/onlinecatering.png';
import backofform from '../../images/backofform.png';
import ReactCardFlip from 'react-card-flip';
import Form from './Form';
import Modal from './Modal';

const CateringForm = () => {

    const [isFlipped, setIsFlipped] = useState(false);

    const handleClick = () => {
        setIsFlipped(!isFlipped);
    };

    return (
        <>
            <CateringFormContainer>
                <CateringFormWrapper>
                    <CateringFormImageContainer>
                        <ReactCardFlip isFlipped={isFlipped} flipDirection="vertical">
                            <StyledFrontCardContainer>
                                <StyledImage
                                    src={background}
                                    alt="Menu"
                                />
                              <StyledFrontCardButton onClick={handleClick}>Submit A Request</StyledFrontCardButton>
                            </StyledFrontCardContainer>

                            <StyledBackCardContainer>
                                <StyledImage 
                                    src={backofform}
                                    alt="Back of Form"
                                />
                            <Form />
                            <StyledModal />
                                <StyledBackCardButton onClick={handleClick}>Click to flip</StyledBackCardButton>
                            </StyledBackCardContainer>
                        </ReactCardFlip>
                    </CateringFormImageContainer>
                </CateringFormWrapper>
            </CateringFormContainer>
        </>
    )
}

export default CateringForm;
